import AppBar from '@material-ui/core/AppBar';
import Link from '@material-ui/core/Link';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import React, { Component } from 'react';
import './ProjectFormOrder.css'
import { connect } from 'react-redux'
import { showProducts, addToOrderer} from '../Redux/actions'
import { Button } from '@material-ui/core';



class ProjectFormOrder extends Component {

  componentWillMount(){
    this.props.showProducts()
    this.props.addToOrderer()
  }

    render(){

        return(
          <div class="root">
             <div class="container">
    <div class="col col-lg-">
          <React.Fragment>
      <h1>List of products</h1>
      <Table class="table">
        <TableHead>
          <TableRow>
            <TableCell>Nombre</TableCell>
            <TableCell>Referencia</TableCell>
            <TableCell>Price</TableCell>
            <TableCell> Action </TableCell>
          </TableRow>
        </TableHead>

        <TableBody>
          {this.props.products.map(row => (
            <TableRow key={row.id}>
              <TableCell>{row.name}</TableCell>
              <TableCell>{row.reference}</TableCell>
              <TableCell>{row.precio}</TableCell>
              <TableCell>

                <Button onClick={clickMe(row)} > Agregar </Button>

                <Button> Eliminar </Button>

              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
      <div className="seeMore">
        <Link color="primary" href="javascript:;">
          Challenger React S.A
        </Link>
      </div>
    </React.Fragment>
    </div>
        </div>




          <div className="root">
          
          <div class="container">
              <div class="col col-lg-">
                    <React.Fragment>
                <h1>List of products</h1>
                <Table class="table">
                  <TableHead>
                    <TableRow>
                      <TableCell>Nombre</TableCell>
                      <TableCell>Precio</TableCell>
                    </TableRow>
                  </TableHead>  

                  <TableBody>
                     {this.props.orderers.map(row => (
                         <TableRow key={row.id}>
                        <TableCell>{row.name}</TableCell>
                       <TableCell>{row.precio}</TableCell>
                   </TableRow>
                       ))}
                   </TableBody>
                  
                </Table>
                <div className="seeMore">
                  <Link color="primary" href="javascript:;">
                    Challenger React S.A
                  </Link>
                </div>
              </React.Fragment>
              </div>
                  </div>
                  </div>
          

            </div>

     




        );
    }

}



function  mapStateToProps(state) {
  return({
    products : state.product.list,
    orderers : state.orderer.list
  })
}

const mapDispatchToProps = {

  showProducts,
  addToOrderer,
  
};

const clickMe = (parameter) => (event) => {
  console.log(parameter)
}

export default connect(mapStateToProps, mapDispatchToProps)(ProjectFormOrder);
