import AppBar from '@material-ui/core/AppBar';
import Link from '@material-ui/core/Link';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import React, { Component } from 'react';
import './ProjectFormOrder.css'
import { connect } from 'react-redux'
import { showProducts, addToOrderer} from '../Redux/actions'
import { Button } from '@material-ui/core';
import { array } from 'prop-types';



class ProjectFormOrder extends Component {

  componentWillMount(){
    this.props.showProducts()
    
  }


  constructor(){
    super()
    this.state = {
      arrayOrderer : []
    }
  }


  clickMeToAdd = (parameter) => (event) => {

     this.setState( {arrayOrderer : this.state.arrayOrderer.concat([parameter])})
    this.props.addToOrderer(this.state.arrayOrderer)
  
  }

  clickMeToDelete = (parameter) => (event) => {

    var array = [this.state.arrayOrderer];
  var index = array.indexOf(parameter)
  if (index !== -1) {
    array.splice(index, 1);
    this.setState({arrayOrderer: array});
  }
  this.props.addToOrderer(this.state.arrayOrderer)
 }

    render(){

        return(
          <div class="root">
             <div class="container">
    <div class="col col-lg-">
          <React.Fragment>
      <h1>List of products</h1>
      <Table class="table">
        <TableHead>
          <TableRow>
            <TableCell>Nombre</TableCell>
            <TableCell>Referencia</TableCell>
            <TableCell>Price</TableCell>
            <TableCell> Action </TableCell>
          </TableRow>
        </TableHead>

        <TableBody>
          {this.props.products.map(row => (
            <TableRow key={row.id}>
              <TableCell>{row.name}</TableCell>
              <TableCell>{row.reference}</TableCell>
              <TableCell>{row.precio}</TableCell>
              <TableCell>

                <Button onClick={this.clickMeToAdd(row)} > Agregar </Button>

                <Button onClick={this.clickMeToDelete(row)}> Eliminar </Button>

              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
      <div className="seeMore">
        <Link color="primary" href="javascript:;">
          Challenger React S.A
        </Link>
      </div>
    </React.Fragment>
    </div>
        </div>




          <div className="root">
          
          <div class="container">
              <div class="col col-lg-">
                    <React.Fragment>
                <h1>List of products</h1>
                <Table class="table">
                  <TableHead>
                    <TableRow>
                      <TableCell>Nombre</TableCell>
                      <TableCell>Precio</TableCell>
                    </TableRow>
                  </TableHead>  

                  <TableBody>
                     {this.props.orderers.map(row => (
                         <TableRow key={row.id}>
                        <TableCell>{row.name}</TableCell>
                       <TableCell>{row.precio}</TableCell>
                   </TableRow>
                       ))}
                   </TableBody>
                  
                </Table>
                <div className="seeMore">
                  <Link color="primary" href="javascript:;">
                    Challenger React S.A
                  </Link>
                </div>
              </React.Fragment>
              </div>
                  </div>
                  </div>
          

            </div>

     




        );
    }

}



function  mapStateToProps(state) {
  return({
    products : state.product.list,
    orderers : state.orderer.price
  })
}

const mapDispatchToProps = {

  showProducts,
  addToOrderer,
  
};



export default connect(mapStateToProps, mapDispatchToProps)(ProjectFormOrder);
